Macro.MacroValue:
                
                
                
                
                

                (>K:AP_WING_LEVELER_OFF)
                (L:Autopilot Mode, number) 0 == if{
                                                    (>K:AP_LOC_HOLD_OFF)
                                                    (>K:AP_APR_HOLD_OFF)

                                                    
                                                } els{
                                                    (L:Autopilot Mode, number) 1 ==  if{
                                                                                           (>K:AP_LOC_HOLD_ON)
                                                                                        } els{
                                                                                            (L:Autopilot Mode, number) 2 == if{
                                                                                                                                (>K:AP_APR_HOLD_ON)
                                                                                                                            } els{
                                                                                                                                (L:Autopilot Mode, number) 3 == if{ (>K:AP_LOC_HOLD_ON) }
                                                                                                                            }
                                                                                        }
                                                }

MouseClick.Script:
(L:ALT_SW_STATE, number) 0 == if{
    
    (A:INDICATED ALTITUDE, feet) (>K:AP_ALT_VAR_SET_ENGLISH)
}
(A:SIM TIME, number) (>L:ALT_SW_STATE, number)

                            (L:I_ALT_HLD_MODE, number) 3 == if{
                                (>K:AP_VS_VAR_DEC)

                                (A:AUTOPILOT VERTICAL HOLD VAR, number) 0 == if{
                                    0 (>K:AP_ALT_VAR_SET_ENGLISH)
                                }
                                (A:AUTOPILOT VERTICAL HOLD VAR, number) 0 < if{
                                    0 (>K:AP_ALT_VAR_SET_ENGLISH)
                                }
                            } els{
                                (>K:AP_PITCH_REF_INC_DN)
                            }

                            (L:I_ALT_HLD_MODE, number) 3 == if{
                                (>K:AP_VS_VAR_INC)

                                (A:AUTOPILOT VERTICAL HOLD VAR, number) 0 == if{
                                    99000 (>K:AP_ALT_VAR_SET_ENGLISH)
                                }
                                (A:AUTOPILOT VERTICAL HOLD VAR, number) 0 > if{
                                    99000 (>K:AP_ALT_VAR_SET_ENGLISH)
                                }
                            } els{
                                (>K:AP_PITCH_REF_INC_UP)
                            }
